int main(void) {
	Solution solution;
	string s, t;
	char answer, result;

	s = "";
	t = "";
	answer = 't';
	result = solution.findTheDifference(s, t);
	assert(answer == result);

	s = "abcd";
	t = "abcde";
	answer = 'e';
	result = solution.findTheDifference(s, t);
	assert(answer == result);

	cout << "\nPassed All\n";
	return 0;
}