// 395. Longest Substring with At Least K Repeating Characters
// https://leetcode.com/problems/longest-substring-with-at-least-k-repeating-characters/
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
// BEGIN: https://discuss.leetcode.com/topic/57134/two-short-c-solutions-3ms-and-6ms
class Solution {
public:
	int longestSubstring(string s, int k) {
		int result = 0;
		const int NASCII = 256, n = s.size();
		if (k > n) return result;
		for (int i = 0; i < n; ) {
			vector<int> hashmap(NASCII, 0);
			int mask = 0;
			int l = i;
			for (int j = i; j < n; j++) {
				hashmap[s[j]]++;
				if (hashmap[s[j]] < k) mask |= (1 << s[j]);
				else mask &= ~(1 << s[j]);
				if (mask == 0) {
					result = max(result, j - i + 1);
					l = j;
				}
			}
			i = l + 1;
		}
		return result;
	}
};
// END: https://discuss.leetcode.com/topic/57134/two-short-c-solutions-3ms-and-6ms
int main(int argc, const char * argv[]) {
	Solution solution;
	cout << solution.longestSubstring("aaabb", 3) << "\tPassed\n";
	cout << solution.longestSubstring("ababbc", 2) << "\tPassed\n";
	cout << solution.longestSubstring("", 100000) << "\tPassed\n";
	cout << "\nPassed All\n";
	return 0;
}