// 395. Longest Substring with At Least K Repeating Characters
// https://leetcode.com/problems/longest-substring-with-at-least-k-repeating-characters/
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
// BEGIN: https://discuss.leetcode.com/topic/57134/two-short-c-solutions-3ms-and-6ms
class Solution {
public:
	int longestSubstring(string s, int k) {
		return this->longestSubstring(s, k, 0, (int)s.size());
	}
private:
	int longestSubstring(const string& s, int k, int begin, int end) {
		const int NASCII = 256, n = s.size();
		if (k > n) return 0;
		vector<int> hashmap(NASCII, 0);
		for (int i = begin; i < end; i++) hashmap[s[i]]++;
		int result = 0;
		for (int i = begin; i < end; ) {
			while (i < end && hashmap[s[i]] < k) i++;
			if (i == end) return result;
			int j = i;
			while (j < end && hashmap[s[j]] >= k) j++;
			if (i == begin && j == end) return j - i;
			result = max(result, this->longestSubstring(s, k, i, j));
			i = j;
		}
		return result;
	}
};
