// 487. Max Consecutive Ones II
// https://leetcode.com/problems/max-consecutive-ones-ii/
#include <iostream>
#include <cassert>
#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
	int findMaxConsecutiveOnes(vector<int>& nums) {
		int result = 0;
		for (size_t i = 0, j = 0, k = 0; i < nums.size(); i++) {
			for (j = i, k = 0; j < nums.size() && k < 2; j++) {
				k += nums[j] == 0;
			}
			if (k >= 2) {
				j--;
			}
			result = max(result, int(j - i));
		}
		return result;
	}
};

