// 529. Minesweeper
// https://leetcode.com/problems/minesweeper/
#include <iostream>
#include <cassert>
#include <vector>
#include <list>
#include <algorithm>
#include <iterator>
using namespace std;

class Solution {
public:
	vector<vector<char>> updateBoard(vector<vector<char>>& board, vector<int>& click) {
		vector<vector<char>> result(board);
		const int p = board.size();
		const int q = board.front().size();
		if (click[0] < 0 || click[0] >= p || click[1] < 0 || click[1] >= q) {
			return result;
		}
		if (result[click[0]][click[1]] == 'M') {
			result[click[0]][click[1]] = 'X';
			return result;
		}
		if (result[click[0]][click[1]] == 'E') {
			const vector<int> dx = {-1, 0, 1, 0, -1, -1, 1, 1};
			const vector<int> dy = {0, -1, 0, 1, -1, 1, -1, 1};
			list<pair<int, int>> queue;
			queue.push_back(make_pair(click[0], click[1]));
			while (!queue.empty()) {
				const pair<int, int> queue_back = queue.back();
				queue.pop_back();
				int cnt = 0;
				for (int i = 0; i < 8; i++) {
					const int x = dx[i] + queue_back.first;
					const int y = dy[i] + queue_back.second;
					if (x >= 0 && x < p && y >= 0 && y < q) {
						if (board[x][y] == 'M') {
							cnt++;
						}
					}
				}
				if (cnt == 0) {
					result[queue_back.first][queue_back.second] = 'B';
					for (int i = 0; i < 8; i++) {
						const int x = dx[i] + queue_back.first;
						const int y = dy[i] + queue_back.second;
						if (x >= 0 && x < p && y >= 0 && y < q && result[x][y] == 'E') {
							queue.push_back(make_pair(x, y));
						}
					}
				}
				else {
					result[queue_back.first][queue_back.second] = '0' + cnt;
				}
			}
		}
		return result;
	}
};

