// 214. Shortest Palindrome
// https://leetcode.com/problems/shortest-palindrome/
// https://discuss.leetcode.com/topic/14526/c-8-ms-kmp-based-o-n-time-o-n-memory-solution
// https://discuss.leetcode.com/topic/27261/clean-kmp-solution-with-super-detailed-explanation/2
#include <iostream>
#include <string>
#include <algorithm>
#include <iterator>
using namespace std;
class Solution {
public:
	string shortestPalindrome(string s) {
		string reverseS(s);
		reverse(begin(reverseS), end(reverseS));
		string p = s + "#" + reverseS;
		vector<size_t> pi(p.size(), 0);
		for (size_t i = 1; i < p.size(); ++i) {
			size_t j = pi[i - 1];
			while (j > 0 && p[i] != p[j]) j = pi[j - 1];
			pi[i] = (j += p[i] == p[j]);	
		}
		return reverseS.substr(0, reverseS.size() - pi.back()) + s;
	}
};
int main(void) {
	Solution solution;
	string s = "aacecaaa";
	cout << solution.shortestPalindrome(s) << "\tPassed\n";
	s = "abcd";
	cout << solution.shortestPalindrome(s) << "\tPassed\n";
	s = "";
	cout << solution.shortestPalindrome(s) << "\tPassed\n";
	cout << "\nPassed All\n";
	return 0;
}